sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === ''|| newValue == oldValue) {
      return;
   }

   var getEmailAddr = new GlideAjax('GetEmailAddress');
	getEmailAddr.addParam('sysparm_name','getEmail');//get email nombre de la funcion del script include
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));//y aqui paso el nombre del usuario

	getEmailAddr.getXML(populateEmailField);//LO ENVIO

	function populateEmailField(response) {
			var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");//RECIBO LA RESPUESTA
		g_form.clearValue('u_requested_for_email');//LIMPIO EL CAMPO
		g_form.setValue('u_requested_for_email',emailFromScriptInclude);//PONGO EL EMAIL AUTOMATICO

	}


}
