sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
		// Crear objetos GlideDateTime para la fecha actual y la fecha pasada.
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// Si la fecha de prueba es anterior a la derecha, devuelva verdadero, si no, devuelva falso
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
